// Convert social media data to the format expected by SocialLinks
const socialLinksData = footerLinks.socialLinks.map(link => ({
  name: link.label,
  url: link.path,
  icon: socialIcons[link.icon] || null
}));

// Convert quick links data to the format expected by QuickLinks
const quickLinksData = footerLinks.quickLinks.map(link => ({
  name: link.label,
  path: link.path
}));

// Convert additional links data to the format expected by QuickLinks
const additionalLinksData = footerLinks.additionalLinks.map(link => ({
  name: link.label,
  path: link.path
}));

// Convert policy links data to the format expected by PolicyLinks
const policyLinksData = footerLinks.policyLinks.map(link => ({
  name: link.label,
  path: link.path
}));

interface FooterProps {
  className?: string;
}

